/************************************************************************* *  Compilation:  javac Reverse.java *  Execution:    java Reverse *  Dependencies: Stack.java StdOut.java StdIn.java * *  Read a sequence of integers from standard input and print them *  in reverse order. * *  % java  java Reverse *  1 2 3 4 5 *  5 *  4 *  3 *  2 *  1 *************************************************************************/package edu.princeton.cs.algorithms.clients.chapter1.stacksqueues;import edu.princeton.cs.algorithms.Stack;import edu.princeton.cs.algorithms.stdlib.StdIn;import edu.princeton.cs.algorithms.stdlib.StdOut;public class Reverse {    public static void main(String[] args) {        Stack<Integer> stack;        stack = new Stack<Integer>();        while (!StdIn.isEmpty())            stack.push(StdIn.readInt());        for (int i : stack)            StdOut.println(i);    }}